definition.script:
pipeline {
    agent any

    stages {
        stage('Deploy') {
                environment { 

                }
                tools {
                    maven 'MAVEN_INSTALLATION'
                }
                steps {
                    sh '''
                        mvn org.apache.maven.plugins:maven-dependency-plugin:2.1:get -DrepoUrl=${params.nexusRepoUrl} \
                        -Dartifact=${params.artifactIdentifier}"
                    '''
                } 
        }
    }
    post {
        always {
            httpRequest acceptType: 'TEXT_PLAIN', consoleLogResponseBody: true, contentType: 'APPLICATION_JSON', httpMode: 'POST', requestBody: "{\"build_url\":\"${env.BUILD_URL}\", \"status\": \"finished\"}", responseHandle: 'NONE', timeout: 10, url:"${params.callBackUrl}", wrapAsMultipart: false, validResponseCodes: '100:500'
            deleteDir() /* clean up our workspace */
        }
        success {
            httpRequest acceptType: 'TEXT_PLAIN', consoleLogResponseBody: true, contentType: 'APPLICATION_JSON', httpMode: 'POST', requestBody: "{\"build_url\":\"${env.BUILD_URL}\", \"status\": \"success\"}", responseHandle: 'NONE', timeout: 10, url:"${params.callBackUrl}", wrapAsMultipart: false, validResponseCodes: '100:500'
            echo 'I succeeeded!'
        }
        unstable {
            httpRequest acceptType: 'TEXT_PLAIN', consoleLogResponseBody: true, contentType: 'APPLICATION_JSON', httpMode: 'POST', requestBody: "{\"build_url\":\"${env.BUILD_URL}\", \"status\": \"unstable\"}", responseHandle: 'NONE', timeout: 10, url:"${params.callBackUrl}", wrapAsMultipart: false, validResponseCodes: '100:500'
            echo 'I am unstable :/'
        }
        failure {
            httpRequest acceptType: 'TEXT_PLAIN', consoleLogResponseBody: true, contentType: 'APPLICATION_JSON', httpMode: 'POST', requestBody: "{\"build_url\":\"${env.BUILD_URL}\", \"status\": \"failure\"}", responseHandle: 'NONE', timeout: 10, url:"${params.callBackUrl}", wrapAsMultipart: false, validResponseCodes: '100:500'
            echo 'I failed :('
        }

    }
}
